/**
 * Type declarations for assets
 */

// 3D models
declare module '*.glb' {
  const content: number;
  export default content;
}

declare module '*.gltf' {
  const content: number;
  export default content;
}

// Images
declare module '*.png' {
  const content: number;
  export default content;
}

declare module '*.jpg' {
  const content: number;
  export default content;
}

declare module '*.jpeg' {
  const content: number;
  export default content;
}

declare module '*.gif' {
  const content: number;
  export default content;
}

declare module '*.svg' {
  const content: number;
  export default content;
}
